package com.aliyouyouzi.mobilesafe.db;import android.content.Context;import android.database.sqlite.SQLiteDatabase;import android.database.sqlite.SQLiteDatabase.CursorFactory;import android.database.sqlite.SQLiteOpenHelper;import android.util.Log;public class BlackSafeOpenHelper extends SQLiteOpenHelper {	private static final String TAG = "BlackSafeOpenHelper";	public BlackSafeOpenHelper(Context context) {		super(context, BlackSafeConstant.DATA_NAME, null,				BlackSafeConstant.BLACK_VERSION);	}	@Override	public void onCreate(SQLiteDatabase db) {		String sql = BlackSafeConstant.SQL.CREATE_SQL;		Log.d(TAG, sql);		db.execSQL(sql);	}	@Override	public void onUpgrade(SQLiteDatabase db, int oldVersion, int newVersion) {	}}